import { useState, useEffect } from "react";
import { useQuery } from "@tanstack/react-query";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Label } from "@/components/ui/label";
import { Card, CardContent } from "@/components/ui/card";
import { FilterOptions } from "@/lib/types";
import { Globe, Building2 } from "lucide-react";

interface GlobalHeaderFilterProps {
  onFilterChange: (filters: { country?: string; sector?: string }) => void;
  initialFilters?: { country?: string; sector?: string };
}

export function GlobalHeaderFilter({ onFilterChange, initialFilters }: GlobalHeaderFilterProps) {
  const [selectedCountry, setSelectedCountry] = useState<string>(initialFilters?.country || "");
  const [selectedSector, setSelectedSector] = useState<string>(initialFilters?.sector || "");

  const { data: filterOptions, isLoading } = useQuery<FilterOptions>({
    queryKey: ["/api/filter-options"],
  });

  useEffect(() => {
    // Load from localStorage on mount
    const savedCountry = localStorage.getItem("globalCountryFilter");
    const savedSector = localStorage.getItem("globalSectorFilter");

    if (savedCountry) {
      setSelectedCountry(savedCountry);
    }
    if (savedSector) {
      setSelectedSector(savedSector);
    }
  }, []);

  const handleCountryChange = (value: string) => {
    const country = value === "all" ? "" : value;
    setSelectedCountry(country);
    localStorage.setItem("globalCountryFilter", country);
    onFilterChange({ country, sector: selectedSector });
  };

  const handleSectorChange = (value: string) => {
    const sector = value === "all" ? "" : value;
    setSelectedSector(sector);
    localStorage.setItem("globalSectorFilter", sector);
    onFilterChange({ country: selectedCountry, sector });
  };

  if (isLoading) {
    return (
      <Card className="border-b border-slate-200 rounded-none">
        <CardContent className="py-4">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            <div className="flex items-center justify-center">
              <div className="animate-pulse text-slate-500">Loading filters...</div>
            </div>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className="border-b border-slate-200 rounded-none bg-gradient-to-r from-slate-50 to-blue-50">
      <CardContent className="py-4">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-2">
              <Globe className="text-primary" size={20} />
              <h2 className="text-sm font-medium text-slate-700">Global Filters</h2>
            </div>
            <div className="flex items-center space-x-6">
              <div className="flex items-center space-x-2">
                <Label htmlFor="global-country" className="text-sm font-medium text-slate-700 whitespace-nowrap">
                  Country:
                </Label>
                <Select value={selectedCountry || "all"} onValueChange={handleCountryChange}>
                  <SelectTrigger id="global-country" className="w-[200px]">
                    <SelectValue placeholder="All Countries" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Countries</SelectItem>
                    {filterOptions?.countries.map(country => (
                      <SelectItem key={country} value={country}>{country}</SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              <div className="flex items-center space-x-2">
                <Label htmlFor="global-sector" className="text-sm font-medium text-slate-700 whitespace-nowrap">
                  Sector:
                </Label>
                <Select value={selectedSector || "all"} onValueChange={handleSectorChange}>
                  <SelectTrigger id="global-sector" className="w-[200px]">
                    <SelectValue placeholder="All Sectors" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Sectors</SelectItem>
                    {filterOptions?.sectors.map(sector => (
                      <SelectItem key={sector} value={sector}>
                        <div className="flex items-center space-x-2">
                          <Building2 size={14} />
                          <span>{sector}</span>
                        </div>
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
            </div>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/investor-filters.t.content:
import { useState, useEffect } from "react";
import { useQuery } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { 
  Filter, 
  X, 
  MapPin, 
  Building2, 
  BarChart3, 
  Zap, 
  Factory, 
  Fuel,
  TrendingUp,
  AlertTriangle,
  Target
} from "lucide-react";
import { InvestorFilters } from "@shared/schema";

// Sector configuration
const SECTORS_CONFIG = {
  "Real Estate": {
    icon: Building2,
    color: "#0a1b3d",
    subSectors: ["Residential", "Commercial", "Hospitality", "Retail", "Mixed-use", "Regional Mall"]
  },
  "Infrastructure": {
    icon: BarChart3,
    color: "#00a7b2",
    subSectors: ["Roads", "Bridges", "Airports", "Ports", "Utilities Infrastructure"]
  },
  "Energy": {
    icon: Zap,
    color: "#f59e0b",
    subSectors: ["Renewable Energy (Solar, Wind)", "Power Plants", "Energy Storage"]
  },
  "Industry": {
    icon: Factory,
    color: "#10b981",
    subSectors: ["Industrial Parks", "Manufacturing Zones", "Logistics Hubs"]
  },
  "Oil & Gas": {
    icon: Fuel,
    color: "#ef4444",
    subSectors: ["Refineries", "Petrochemical Plants", "Distribution Facilities"]
  }
};

const STATUSES = ["Planning", "Under Construction", "Nearing Completion", "Completed / Operational"];

interface InvestorFiltersProps {
  filters: InvestorFilters;
  onFiltersChange: (filters: InvestorFilters) => void;
  onGetInsights: () => void;
}

export function InvestorFiltersComponent({ filters, onFiltersChange, onGetInsights }: InvestorFiltersProps) {
  const [selectedCountry, setSelectedCountry] = useState(filters.country || "Saudi Arabia");
  const [selectedCity, setSelectedCity] = useState(filters.city || "");
  const [selectedDistrict, setSelectedDistrict] = useState(filters.district || "");
  const [selectedSector, setSelectedSector] = useState(filters.sector || "");
  const [selectedSubSector, setSelectedSubSector] = useState(filters.subSector || "");
  const [selectedStatus, setSelectedStatus] = useState(filters.status || "");

  // Query for cities based on selected country
  const { data: cities = [] } = useQuery<string[]>({
    queryKey: ["/api/cities", selectedCountry],
    enabled: !!selectedCountry,
  });

  // Query for districts based on selected country and city
  const { data: districts = [] } = useQuery<string[]>({
    queryKey: ["/api/districts", selectedCountry, selectedCity],
    enabled: !!selectedCountry && !!selectedCity,
  });

  // Update filters when selections change
  useEffect(() => {
    const newFilters: InvestorFilters = {
      country: selectedCountry || undefined,
      city: selectedCity || undefined,
      district: selectedDistrict || undefined,
      sector: selectedSector || undefined,
      subSector: selectedSubSector || undefined,
      status: selectedStatus as any || undefined,
    };
    onFiltersChange(newFilters);
  }, [selectedCountry, selectedCity, selectedDistrict, selectedSector, selectedSubSector, selectedStatus, onFiltersChange]);

  const handleCountryChange = (value: string) => {
    setSelectedCountry(value);
    setSelectedCity("");
    setSelectedDistrict("");
  };

  const handleCityChange = (value: string) => {
    setSelectedCity(value);
    setSelectedDistrict("");
  };

  const handleSectorChange = (value: string) => {
    setSelectedSector(value);
    setSelectedSubSector("");
  };

  const clearAllFilters = () => {
    setSelectedCountry("Saudi Arabia");
    setSelectedCity("");
    setSelectedDistrict("");
    setSelectedSector("");
    setSelectedSubSector("");
    setSelectedStatus("");
  };

  const getActiveFilterCount = () => {
    return [selectedCity, selectedDistrict, selectedSector, selectedSubSector, selectedStatus]
      .filter(filter => filter && filter !== "").length;
  };

  const selectedSectorConfig = selectedSector ? SECTORS_CONFIG[selectedSector as keyof typeof SECTORS_CONFIG] : null;

  return (
    <Card className="investor-filters">
      <CardHeader>
        <CardTitle className="flex items-center justify-between">
          <span className="flex items-center">
            <Filter className="mr-2" size={18} />
            Investment Criteria
          </span>
          <div className="flex items-center space-x-2">
            {getActiveFilterCount() > 0 && (
              <Badge variant="secondary" className="bg-primary/10 text-primary">
                {getActiveFilterCount()} filters
              </Badge>
            )}
            <Button 
              variant="outline" 
              size="sm" 
              onClick={onGetInsights}
              className="bg-primary text-white hover:bg-primary/90"
            >
              <TrendingUp className="mr-1" size={14} />
              Get Insights
            </Button>
          </div>
        </CardTitle>
      </CardHeader>
      <CardContent className="space-y-6">

        {/* Location Selection */}
        <div>
          <div className="flex items-center mb-3">
            <MapPin className="mr-2 text-primary" size={16} />
            <span className="text-sm font-medium text-slate-700">Location Targeting</span>
          </div>

          <div className="space-y-3">
            {/* Country (Fixed to Saudi Arabia) */}
