client/src/components/investor-insights..content:
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { 
  TrendingUp, 
  TrendingDown, 
  MapPin, 
  DollarSign, 
  Users, 
  AlertTriangle,
  Target,
  Activity,
  BarChart3
} from "lucide-react";
import { Project } from "@shared/schema";

interface InvestorInsightsProps {
  projects: Project[];
  selectedLocation?: string;
  selectedSector?: string;
  selectedSubSector?: string;
}

export function InvestorInsights({ projects, selectedLocation, selectedSector, selectedSubSector }: InvestorInsightsProps) {
  // Calculate market metrics
  const totalInvestment = projects.reduce((sum, project) => sum + project.investment, 0);
  const averageROI = projects.length > 0 
    ? projects.reduce((sum, project) => sum + (project.expectedRoi || 0), 0) / projects.length 
    : 0;

  const statusDistribution = projects.reduce((acc, project) => {
    acc[project.status] = (acc[project.status] || 0) + 1;
    return acc;
  }, {} as Record<string, number>);

  const investmentRanges = {
    small: projects.filter(p => p.investment < 100).length,
    medium: projects.filter(p => p.investment >= 100 && p.investment < 500).length,
    large: projects.filter(p => p.investment >= 500).length
  };

  // Market gap analysis (simulated based on project data)
  const getMarketGaps = () => {
    const gaps = [];

    if (selectedLocation && selectedSector) {
      if (projects.length < 3) {
        gaps.push({
          type: "opportunity",
          title: "Limited Competition",
          description: `Only ${projects.length} active projects in ${selectedLocation} ${selectedSector}`,
          confidence: "High",
          impact: "Major"
        });
      }

      if (selectedSubSector === "Regional Mall") {
        gaps.push({
          type: "gap",
          title: "Retail Infrastructure Gap",
          description: "Growing population but limited retail space per capita",
          confidence: "Medium",
          impact: "Significant"
        });
      }

      if (averageROI > 20) {
        gaps.push({
          type: "trend",
          title: "High ROI Market",
          description: `Average ROI of ${averageROI.toFixed(1)}% indicates strong market demand`,
          confidence: "High",
          impact: "Positive"
        });
      }
    }

    return gaps;
  };

  const marketGaps = getMarketGaps();

  if (projects.length === 0) {
    return (
      <Card>
        <CardContent className="p-8 text-center">
          <AlertTriangle className="mx-auto h-12 w-12 text-slate-400 mb-4" />
          <h3 className="text-lg font-medium text-slate-900 mb-2">No Data Available</h3>
          <p className="text-slate-600">
            Please select your investment criteria to view market insights and opportunities.
          </p>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="space-y-6">

      {/* Market Overview Cards */}
      <div className="grid md:grid-cols-4 gap-4">
        <Card>
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-slate-600">Total Projects</p>
                <p className="text-2xl font-bold text-slate-900">{projects.length}</p>
              </div>
              <Activity className="h-8 w-8 text-primary" />
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-slate-600">Total Investment</p>
                <p className="text-2xl font-bold text-slate-900">${totalInvestment.toFixed(0)}M</p>
              </div>
              <DollarSign className="h-8 w-8 text-green-500" />
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-slate-600">Avg. ROI</p>
                <p className="text-2xl font-bold text-slate-900">{averageROI.toFixed(1)}%</p>
              </div>
              <TrendingUp className="h-8 w-8 text-blue-500" />
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-slate-600">Market Confidence</p>
                <p className="text-2xl font-bold text-slate-900">High</p>
              </div>
              <Target className="h-8 w-8 text-orange-500" />
            </div>
          </CardContent>
        </Card>
      </div>

      {/* District-Level Insights */}
      {selectedLocation && (
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center">
              <MapPin className="mr-2" size={18} />
              {selectedLocation} Market Analysis
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid md:grid-cols-2 gap-6">

              {/* Project Status Distribution */}
              <div>
                <h4 className="text-sm font-medium text-slate-700 mb-3">Project Pipeline</h4>
                <div className="space-y-2">
                  {Object.entries(statusDistribution).map(([status, count]) => {
                    const percentage = (count / projects.length) * 100;
                    return (
                      <div key={status}>
                        <div className="flex justify-between text-sm">
                          <span className="text-slate-600">{status}</span>
                          <span className="font-medium">{count} projects</span>
                        </div>
                        <Progress value={percentage} className="h-2" />
                      </div>
                    );
                  })}
                </div>
              </div>

              {/* Investment Size Distribution */}
              <div>
                <h4 className="text-sm font-medium text-slate-700 mb-3">Investment Distribution</h4>
                <div className="space-y-3">
                  <div className="flex justify-between items-center">
                    <span className="text-sm text-slate-600">Small (&lt;$100M)</span>
                    <Badge variant="outline">{investmentRanges.small} projects</Badge>
                  </div>
                  <div className="flex justify-between items-center">
                    <span className="text-sm text-slate-600">Medium ($100M-$500M)</span>
                    <Badge variant="outline">{investmentRanges.medium} projects</Badge>
                  </div>
                  <div className="flex justify-between items-center">
                    <span className="text-sm text-slate-600">Large (&gt;$500M)</span>
                    <Badge variant="outline">{investmentRanges.large} projects</Badge>
                  </div>
                </div>
              </div>
            </div>
          </CardContent>
        </Card>
      )}

      {/* Market Gap Indicators */}
      {marketGaps.length > 0 && (
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center">
              <BarChart3 className="mr-2" size={18} />
              Market Gap Analysis
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              {marketGaps.map((gap, index) => {
                const getIcon = () => {
                  switch (gap.type) {
                    case "opportunity": return <TrendingUp className="text-green-500" size={16} />;
                    case "gap": return <AlertTriangle className="text-orange-500" size={16} />;
                    case "trend": return <Activity className="text-blue-500" size={16} />;
                    default: return <Target className="text-slate-500" size={16} />;
                  }
                };

                const getBadgeColor = () => {
                  switch (gap.impact) {
                    case "Major": return "bg-green-100 text-green-800";
                    case "Significant": return "bg-orange-100 text-orange-800";
                    case "Positive": return "bg-blue-100 text-blue-800";
                    default: return "bg-slate-100 text-slate-800";
                  }
                };

                return (
                  <div key={index} className="flex items-start space-x-3 p-4 bg-slate-50 rounded-lg">
                    <div className="mt-0.5">{getIcon()}</div>
                    <div className="flex-1">
                      <div className="flex items-center justify-between mb-1">
                        <h4 className="font-medium text-slate-900">{gap.title}</h4>
                        <div className="flex space-x-2">
                          <Badge variant="outline" className="text-xs">{gap.confidence} Confidence</Badge>
                          <Badge className={`text-xs ${getBadgeColor()}`}>{gap.impact} Impact</Badge>
                        </div>
                      </div>
                      <p className="text-sm text-slate-600">{gap.description}</p>
                    </div>
                  </div>
                );
              })}
            </div>
          </CardContent>
        </Card>
      )}

      {/* Investment Recommendation */}
      {selectedSector && selectedLocation && (
        <Card className="border-primary/20 bg-primary/5">
          <CardContent className="p-6">
            <div className="flex items-start space-x-3">
              <Target className="text-primary mt-1" size={20} />
              <div>
                <h4 className="font-semibold text-slate-900 mb-2">Investment Recommendation</h4>
                <p className="text-slate-700 mb-3">
                  Based on the current market data for {selectedSector} in {selectedLocation}, we recommend considering 
                  {selectedSubSector ? ` ${selectedSubSector} projects` : ' projects'} with the following criteria:
                </p>
                <ul className="text-sm text-slate-600 space-y-1">
                  <li>â€¢ Target ROI: {(averageROI * 0.9).toFixed(1)}% - {(averageROI * 1.1).toFixed(1)}%</li>
                  <li>â€¢ Investment range: $100M - $500M for optimal market positioning</li>
                  <li>â€¢ Project status: Planning phase for maximum appreciation potential</li>
                  <li>â€¢ Market timing: High confidence based on current indicators</li>
                </ul>
              </div>
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

client/src/components/market-indicator.t.content:
import { Card, CardContent } from "@/components/ui/card";
import { TrendingUp, TrendingDown, AlertTriangle } from "lucide-react";
import { MarketIndicator as MarketIndicatorType } from "@shared/schema";
import { cn } from "@/lib/utils";

interface MarketIndicatorProps {
  indicator: MarketIndicatorType;
}

export function MarketIndicator({ indicator }: MarketIndicatorProps) {
  const getIcon = () => {
    switch (indicator.type) {
      case "opportunity":
        return <TrendingUp className="text-green-600" size={20} />;
      case "trend":
        return <TrendingUp className="text-blue-600" size={20} />;
      case "alert":
        return <AlertTriangle className="text-orange-600" size={20} />;
      default:
        return null;
    }
  };

  const getValueColor = () => {
    switch (indicator.type) {
      case "opportunity":
        return "text-green-600";
      case "trend":
        return "text-blue-600";
      case "alert":
        return "text-orange-600";
      default:
        return "text-slate-600";
    }
  };

  return (
    <Card className={cn("market-indicator", indicator.type)}>
      <CardContent className="p-6">
        <div className="flex items-center">
          <div className="flex-shrink-0">
            <div className={cn(
              "w-8 h-8 rounded-full flex items-center justify-center",
              indicator.type === "opportunity" && "bg-green-100",
              indicator.type === "trend" && "bg-blue-100",
              indicator.type === "alert" && "bg-orange-100"
            )}>
              {getIcon()}
            </div>
          </div>
          <div className="ml-4">
            <h3 className="text-sm font-medium text-slate-900">{indicator.title}</h3>
            <p className="text-xs text-slate-600 mt-1">{indicator.description}</p>
          </div>
        </div>
        <div className="mt-4">
          <div className={cn("flex items-center text-xs", getValueColor())}>
            <span className="font-medium">{indicator.value}</span>
            <span className="ml-1">{indicator.valueLabel}</span>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/project-card.tsx.content:
import { Card, CardContent } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Heart, Share, Info, MapPin, Building } from "lucide-react";
import { Project } from "@shared/schema";
import { cn } from "@/lib/utils";

interface ProjectCardProps {
  project: Project;
  onToggleFavorite?: (projectId: number) => void;
  onShare?: (project: Project) => void;
  onViewDetails?: (project: Project) => void;
  isFavorite?: boolean;
}

export function ProjectCard({ 
  project, 
  onToggleFavorite, 
  onShare, 
  onViewDetails,
  isFavorite = false 
}: ProjectCardProps) {
  const getStatusColor = (status: string) => {
    switch (status.toLowerCase()) {
      case "under construction":
        return "bg-green-100 text-green-800";
      case "planning":
        return "bg-blue-100 text-blue-800";
      case "completed":
        return "bg-orange-100 text-orange-800";
      case "on hold":
        return "bg-gray-100 text-gray-800";
      default:
        return "bg-slate-100 text-slate-800";
    }
  };

  const formatCurrency = (amount: number) => {
    return `$${amount}M`;
  };

  const formatSize = (size: number) => {
    return `${(size / 1000).toFixed(0)}K sq ft`;
  };

  const getRoi = () => {
    if (project.status === "Completed" && project.currentRoi) {
      return project.currentRoi;
    }
    return project.expectedRoi;
  };

  return (
    <Card className="project-card">
      {project.imageUrl && (
        <img 
          src={project.imageUrl} 
          alt={project.name}
          className="w-full h-48 object-cover rounded-t-lg"
        />
      )}

      <CardContent className="p-6">
        <div className="flex items-center justify-between mb-3">
          <h3 className="text-lg font-semibold text-slate-900 truncate">{project.name}</h3>
          <Badge className={cn("text-xs", getStatusColor(project.status))}>
            {project.status}
          </Badge>
        </div>

        <div className="text-sm text-slate-600 mb-4 space-y-1">
          <div className="flex items-center">
            <MapPin className="mr-2 text-slate-400" size={14} />
            <span>{project.location}</span>
          </div>
          <div className="flex items-center">
            <Building className="mr-2 text-slate-400" size={14} />
            <span>{project.projectType} â€¢ {project.sector}</span>
          </div>
        </div>

        <div className="grid grid-cols-2 gap-4 text-sm mb-4">
          <div>
            <span className="text-slate-500">Investment:</span>
            <div className="font-semibold text-slate-900">{formatCurrency(project.investment)}</div>
          </div>
          <div>
            <span className="text-slate-500">
              {project.status === "Completed" ? "Current ROI:" : "Expected ROI:"}
            </span>
            <div className="font-semibold text-green-600">
              {getRoi() ? `${getRoi()?.toFixed(1)}%` : "N/A"}
            </div>
          </div>
        </div>

        {project.size && (
          <div className="text-sm text-slate-500 mb-4">
            Size: {formatSize(project.size)}
          </div>
        )}

        <div className="flex items-center justify-between">
          <span className="text-xs text-slate-500">
            {project.status === "Completed" ? `Completed: ${project.completionDate}` : `Completion: ${project.completionDate}`}
          </span>
          <div className="flex space-x-2">
            <Button
              variant="ghost"
              size="sm"
              onClick={() => onToggleFavorite?.(project.id)}
              className={cn(
                "text-primary hover:text-primary",
                isFavorite && "text-red-500 hover:text-red-600"
              )}
            >
              <Heart size={16} className={isFavorite ? "fill-current" : ""} />
            </Button>
            <Button
              variant="ghost"
              size="sm"
              onClick={() => onShare?.(project)}
              className="text-primary hover:text-primary"
            >
              <Share size={16} />
            </Button>
            <Button
              variant="ghost"
              size="sm"
              onClick={() => onViewDetails?.(project)}
              className="text-primary hover:text-primary"
            >
              <Info size={16} />
            </Button>
          </div>
        </div>

        {/* Project Features */}
        {project.features && project.features.length > 0 && (
          <div className="mt-4 pt-4 border-t border-slate-100">
            <div className="flex flex-wrap gap-1">
              {project.features.slice(0, 3).map((feature, index) => (
                <Badge key={index} variant="outline" className="text-xs">
                  {feature}
                </Badge>
              ))}
              {project.features.length > 3 && (
                <Badge variant="outline" className="text-xs">
                  +{project.features.length - 3} more
                </Badge>
              )}
            </div>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

client/src/components/project-comparison.content:
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Plus, X } from "lucide-react";
import { Project } from "@shared/schema";
import { cn } from "@/lib/utils";

interface ProjectComparisonProps {
  projects: Project[];
  onAddProject: () => void;
  onRemoveProject: (projectId: number) => void;
}

export function ProjectComparison({ projects, onAddProject, onRemoveProject }: ProjectComparisonProps) {
  const getStatusColor = (status: string) => {
    switch (status.toLowerCase()) {
      case "under construction":
        return "bg-green-100 text-green-800";
      case "planning":
        return "bg-blue-100 text-blue-800";
      case "completed":
        return "bg-orange-100 text-orange-800";
      default:
        return "bg-slate-100 text-slate-800";
    }
  };

  const formatCurrency = (amount: number) => {
    return `$${amount}M`;
  };

  const formatSize = (size: number | null) => {
    if (!size) return "N/A";
    return `${(size / 1000).toFixed(0)}K sq ft`;
  };

  const getRoi = (project: Project) => {
    if (project.status === "Completed" && project.currentRoi) {
      return `${project.currentRoi.toFixed(1)}%`;
    }
    if (project.expectedRoi) {
      return `${project.expectedRoi.toFixed(1)}%`;
    }
    return "N/A";
  };

  return (
    <Card>
      <CardHeader>
        <div className="flex items-center justify-between">
          <CardTitle>Project Comparison</CardTitle>
          <Button variant="outline" size="sm" onClick={onAddProject}>
            <Plus className="mr-1" size={16} />
            Add Project
          </Button>
        </div>
      </CardHeader>
      <CardContent>
        {projects.length === 0 ? (
          <div className="text-center py-8 text-slate-500">
            <p>No projects selected for comparison</p>
            <p className="text-sm mt-1">Add projects to compare side-by-side</p>
          </div>
        ) : (
