import { useState, useEffect } from "react";
import { useQuery } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Checkbox } from "@/components/ui/checkbox";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { Grid, List, Download, FileSpreadsheet, Bookmark, Filter, X } from "lucide-react";
import { ProjectCard } from "@/components/project-card";
import { MarketIndicator } from "@/components/market-indicator";
import { ProjectComparison } from "@/components/project-comparison";
import { GlobalHeaderFilter } from "@/components/global-header-filter";
import { InvestorFiltersComponent } from "@/components/investor-filters";
import { InvestorInsights } from "@/components/investor-insights";
import { Project, MarketIndicator as MarketIndicatorType, SearchFilters, InvestorFilters } from "@shared/schema";
import { UserRole, FilterOptions } from "@/lib/types";
import { cn } from "@/lib/utils";
import { useToast } from "@/hooks/use-toast";

export default function Dashboard() {
  const { toast } = useToast();
  const [selectedRole, setSelectedRole] = useState<UserRole | null>(null);
  const [searchFilters, setSearchFilters] = useState<SearchFilters>({});
  const [viewMode, setViewMode] = useState<"grid" | "list">("grid");
  const [sortBy, setSortBy] = useState("relevance");
  const [activeFilters, setActiveFilters] = useState<SearchFilters>({});
  const [comparisonProjects, setComparisonProjects] = useState<Project[]>([]);
  const [favoriteProjects, setFavoriteProjects] = useState<number[]>([]);
  const [globalFilters, setGlobalFilters] = useState<{ country?: string; sector?: string }>({});
  const [investorFilters, setInvestorFilters] = useState<InvestorFilters>({ country: "Saudi Arabia" });
  const [showInsights, setShowInsights] = useState(false);

  useEffect(() => {
    const role = localStorage.getItem("selectedRole") as UserRole;
    const filters = localStorage.getItem("searchFilters");

    if (role) {
      setSelectedRole(role);
    }

    if (filters) {
      const parsedFilters = JSON.parse(filters);
      setSearchFilters(parsedFilters);
      setActiveFilters(parsedFilters);
    }

    // Load global filters from localStorage
    const savedCountry = localStorage.getItem("globalCountryFilter");
    const savedSector = localStorage.getItem("globalSectorFilter");

    if (savedCountry || savedSector) {
      setGlobalFilters({
        country: savedCountry || undefined,
        sector: savedSector || undefined,
      });
    }

    // Set default country to Saudi Arabia for investors
    if (role === "investor" && !savedCountry) {
      setGlobalFilters(prev => ({ ...prev, country: "Saudi Arabia" }));
      setInvestorFilters(prev => ({ ...prev, country: "Saudi Arabia" }));
    }
  }, []);

  // Use investor filters for investor role, otherwise use regular filters
  const queryFilters = selectedRole === "investor" ? investorFilters : { ...activeFilters, ...globalFilters };

  const { data: projects = [], isLoading: isLoadingProjects } = useQuery<Project[]>({
    queryKey: ["/api/projects", queryFilters],
    enabled: true,
  });

  const { data: marketIndicators = [], isLoading: isLoadingIndicators } = useQuery<MarketIndicatorType[]>({
    queryKey: ["/api/market-indicators"],
  });

  const { data: filterOptions } = useQuery<FilterOptions>({
    queryKey: ["/api/filter-options"],
  });

  const handleFilterChange = (key: keyof SearchFilters, value: string | number | boolean | null) => {
    const newFilters = {
      ...activeFilters,
      [key]: value === null || value === "" || value === "All" ? undefined : value
    };
    setActiveFilters(newFilters);
  };

  const handleGlobalFilterChange = (newGlobalFilters: { country?: string; sector?: string }) => {
    setGlobalFilters(newGlobalFilters);
  };

  const removeFilter = (key: keyof SearchFilters) => {
    const newFilters = { ...activeFilters };
    delete newFilters[key];
    setActiveFilters(newFilters);
  };

  const handleToggleFavorite = (projectId: number) => {
    setFavoriteProjects(prev => 
      prev.includes(projectId) 
        ? prev.filter(id => id !== projectId)
        : [...prev, projectId]
    );
  };

  const handleAddToComparison = (project: Project) => {
    if (comparisonProjects.length >= 4) {
      toast({
        title: "Comparison Limit",
        description: "You can compare up to 4 projects at a time.",
        variant: "destructive",
      });
      return;
    }

    if (comparisonProjects.find(p => p.id === project.id)) {
      toast({
        title: "Already Added",
        description: "This project is already in your comparison.",
        variant: "destructive",
      });
      return;
    }

    setComparisonProjects(prev => [...prev, project]);
    toast({
      title: "Added to Comparison",
      description: `${project.name} has been added to comparison.`,
    });
  };

  const handleRemoveFromComparison = (projectId: number) => {
    setComparisonProjects(prev => prev.filter(p => p.id !== projectId));
  };

  const handleExportData = () => {
    toast({
      title: "Export Started",
      description: "Your project data is being prepared for download.",
    });
  };

  const handleSaveSearch = () => {
    toast({
      title: "Search Saved",
      description: "Your search criteria has been saved to your profile.",
    });
  };

  const handleInvestorFiltersChange = (filters: InvestorFilters) => {
    setInvestorFilters(filters);
  };

  const handleGetInsights = () => {
    setShowInsights(true);
    toast({
      title: "Market Analysis Generated",
      description: "District-level insights and market gap indicators are now available.",
    });
  };

  const getActiveFilterEntries = () => {
    return Object.entries(activeFilters).filter(([, value]) => value !== undefined);
  };

  if (isLoadingProjects || isLoadingIndicators) {
    return (
      <div className="min-h-screen bg-slate-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-primary mx-auto"></div>
          <p className="mt-4 text-slate-600">Loading dashboard...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-slate-50">
      {/* Global Header Filter */}
      <GlobalHeaderFilter 
        onFilterChange={handleGlobalFilterChange}
        initialFilters={globalFilters}
      />

      <div className="flex">
        {/* Sidebar Filters */}
        <div className="filter-sidebar">
          <div className="p-6">
            {selectedRole === "investor" ? (
              <InvestorFiltersComponent 
                filters={investorFilters}
                onFiltersChange={handleInvestorFiltersChange}
                onGetInsights={handleGetInsights}
              />
            ) : (
              <>
                <div className="flex items-center justify-between mb-6">
                  <h2 className="text-lg font-semibold text-slate-900">Filters</h2>
                  <Filter size={18} className="text-slate-400" />
                </div>

            {/* Active Filters */}
            {getActiveFilterEntries().length > 0 && (
              <div className="mb-6">
                <h3 className="text-sm font-medium text-slate-700 mb-3">Active Filters</h3>
                <div className="space-y-2">
                  {getActiveFilterEntries().map(([key, value]) => (
                    <Badge key={key} variant="secondary" className="flex items-center justify-between">
                      <span className="text-xs">{String(value)}</span>
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => removeFilter(key as keyof SearchFilters)}
                        className="ml-1 h-auto p-0 text-slate-500 hover:text-slate-700"
                      >
                        <X size={12} />
                      </Button>
                    </Badge>
                  ))}
                </div>
              </div>
            )}

            {/* Filter Sections */}
            <div className="space-y-6">
              <div>
                <h3 className="text-sm font-medium text-slate-700 mb-3">Project Type</h3>
                <div className="space-y-2">
                  {filterOptions?.projectTypes.map(type => (
                    <div key={type} className="flex items-center space-x-2">
                      <Checkbox
                        id={`type-${type}`}
                        checked={activeFilters.projectType === type}
                        onCheckedChange={(checked) => 
                          handleFilterChange("projectType", checked ? type : null)
                        }
                      />
                      <label 
                        htmlFor={`type-${type}`}
                        className="text-sm text-slate-600 cursor-pointer"
                      >
                        {type}
                      </label>
                    </div>
                  ))}
                </div>
              </div>

              <Separator />

              <div>
                <h3 className="text-sm font-medium text-slate-700 mb-3">Status</h3>
                <div className="space-y-2">
                  {filterOptions?.statuses.map(status => (
                    <div key={status} className="flex items-center space-x-2">
                      <Checkbox
                        id={`status-${status}`}
                        checked={activeFilters.status === status}
                        onCheckedChange={(checked) => 
                          handleFilterChange("status", checked ? status : null)
                        }
                      />
                      <label 
                        htmlFor={`status-${status}`}
                        className="text-sm text-slate-600 cursor-pointer"
                      >
                        {status}
                      </label>
                    </div>
                  ))}
                </div>
              </div>

              <Separator />

              <div>
                <h3 className="text-sm font-medium text-slate-700 mb-3">Special Features</h3>
                <div className="space-y-2">
                  <div className="flex items-center space-x-2">
                    <Checkbox
                      id="luxury"
                      checked={activeFilters.isLuxury === true}
                      onCheckedChange={(checked) => 
                        handleFilterChange("isLuxury", checked ? true : null)
                      }
                    />
                    <label htmlFor="luxury" className="text-sm text-slate-600 cursor-pointer">
                      Luxury Projects
                    </label>
                  </div>
                  <div className="flex items-center space-x-2">
                    <Checkbox
                      id="waterfront"
                      checked={activeFilters.isWaterfront === true}
                      onCheckedChange={(checked) => 
                        handleFilterChange("isWaterfront", checked ? true : null)
                      }
                    />
                    <label htmlFor="waterfront" className="text-sm text-slate-600 cursor-pointer">
                      Waterfront Properties
                    </label>
                  </div>
                  <div className="flex items-center space-x-2">
                    <Checkbox
                      id="sustainable"
                      checked={activeFilters.isSustainable === true}
                      onCheckedChange={(checked) => 
                        handleFilterChange("isSustainable", checked ? true : null)
                      }
                    />
                    <label htmlFor="sustainable" className="text-sm text-slate-600 cursor-pointer">
                      Sustainable Development
                    </label>
                  </div>
                </div>
              </div>
            </div>
              </>
            )}
          </div>
        </div>

        {/* Main Content */}
        <div className="flex-1 ml-80">
          <div className="p-6">
            {/* Header */}
            <div className="flex items-center justify-between mb-6">
              <div>
                <h1 className="text-2xl font-bold text-slate-900">Project Dashboard</h1>
                <p className="text-slate-600 mt-1">
                  Found <span className="font-medium">{projects.length} projects</span> matching your criteria
                </p>
              </div>
              <div className="flex items-center space-x-3">
                <div className="flex items-center space-x-2">
                  <span className="text-sm text-slate-600">View:</span>
                  <Button
                    variant={viewMode === "grid" ? "default" : "outline"}
                    size="sm"
                    onClick={() => setViewMode("grid")}
                  >
                    <Grid size={16} />
                  </Button>
                  <Button
                    variant={viewMode === "list" ? "default" : "outline"}
                    size="sm"
                    onClick={() => setViewMode("list")}
                  >
                    <List size={16} />
                  </Button>
                </div>
                <Select value={sortBy} onValueChange={setSortBy}>
                  <SelectTrigger className="w-[180px]">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="relevance">Sort by: Relevance</SelectItem>
                    <SelectItem value="investment">Investment Size</SelectItem>
                    <SelectItem value="completion">Completion Date</SelectItem>
                    <SelectItem value="roi">ROI Potential</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>

            {/* Investor Insights or Market Indicators */}
            {selectedRole === "investor" && showInsights ? (
              <div className="mb-8">
                <InvestorInsights 
                  projects={projects}
                  selectedLocation={`${investorFilters.city || investorFilters.country || ""}${investorFilters.district ? ` â†’ ${investorFilters.district}` : ""}`}
                  selectedSector={investorFilters.sector}
                  selectedSubSector={investorFilters.subSector}
                />
              </div>
            ) : (
              <div className="grid lg:grid-cols-3 gap-6 mb-8">
                {marketIndicators.map((indicator) => (
                  <MarketIndicator key={indicator.id} indicator={indicator} />
                ))}
              </div>
            )}

            {/* Project Comparison Module */}
            <div className="mb-8">
              <ProjectComparison
                projects={comparisonProjects}
                onAddProject={() => {
                  toast({
                    title: "Add Project",
                    description: "Click the info button on any project card to add it to comparison.",
                  });
                }}
                onRemoveProject={handleRemoveFromComparison}
              />
            </div>

            {/* Project Grid */}
            <div className={cn(
              "grid gap-6 mb-8",
              viewMode === "grid" ? "lg:grid-cols-2 xl:grid-cols-3" : "grid-cols-1"
            )}>
              {projects.map((project) => (
                <ProjectCard
                  key={project.id}
                  project={project}
                  onToggleFavorite={handleToggleFavorite}
                  onViewDetails={handleAddToComparison}
                  isFavorite={favoriteProjects.includes(project.id)}
                />
              ))}
            </div>

            {projects.length === 0 && (
              <Card className="text-center py-12">
                <CardContent>
                  <h3 className="text-lg font-medium text-slate-900 mb-2">No Projects Found</h3>
                  <p className="text-slate-600">
                    Try adjusting your search criteria or removing some filters.
                  </p>
                </CardContent>
              </Card>
            )}

            {/* Export & Save Options */}
            <div className="flex items-center justify-center">
              <div className="flex items-center space-x-4">
                <Button variant="outline" onClick={handleExportData}>
                  <Download className="mr-2" size={16} />
                  Export Report
                </Button>
                <Button variant="outline" onClick={handleExportData}>
                  <FileSpreadsheet className="mr-2" size={16} />
                  Export Data
                </Button>
                <Button onClick={handleSaveSearch}>
                  <Bookmark className="mr-2" size={16} />
                  Save Search
                </Button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/pages/homepage.tsx.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Link, useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Building, Search, MapPin, DollarSign, Calendar, Newspaper, TrendingUp, Star } from "lucide-react";
import logoPath from "@assets/Colored SI Logo_1752857295177.png";

interface Project {
  id: string;
  name: string;
  location: string;
  sector: string;
  status: string;
  investment: number;
  completionDate: string;
  description: string;
}

interface Sector {
  name: string;
  projectCount: number;
  growthRate: string;
  averageValue: number;
}

interface HomepageProps {
  userRole?: string;
}

export default function Homepage() {
  const [searchTerm, setSearchTerm] = useState("");
  const [, setLocation] = useLocation();

  // Get user role from localStorage with explicit logging
  const userRole = localStorage.getItem("selectedRole") || "";
  console.log("Homepage - detected userRole:", userRole);

  // Fetch latest projects
  const { data: latestProjects = [] } = useQuery<Project[]>({
    queryKey: ['/api/projects'],
  });

  // Fetch trending sectors
  const { data: trendingSectors = [] } = useQuery<Sector[]>({
    queryKey: ['/api/trending-sectors'],
  });

  // Featured projects (using first 3 from all projects)
  const featuredProjects = latestProjects.slice(0, 3);

  const handleSearch = (e: React.FormEvent) => {
    e.preventDefault();
    if (searchTerm.trim()) {
      setLocation(`/search?q=${encodeURIComponent(searchTerm)}`);
    }
  };

  const getStartedPath = () => {
    switch (userRole) {
      case "contractor": return "/contractor-dashboard";
      case "investor": return "/investor-dashboard";
      case "consultant": return "/consultant-dashboard";
      case "developer": return "/developer-dashboard";
      case "supplier": return "/supplier-dashboard";
      default: return "/search";
    }
  };

  const getRoleWelcomeMessage = () => {
    const roleNames = {
      contractor: "Ahmed",
      investor: "Sarah",
      consultant: "Michael",
      developer: "Fatima",
      supplier: "Omar"
    };

    const name = roleNames[userRole as keyof typeof roleNames] || "User";

    switch (userRole) {
      case "contractor":
        return {
          title: `Welcome ${name}!`,
          subtitle: "Discover active projects and bidding opportunities",
          cta: "Find Projects"
        };
      case "investor":
        return {
          title: `Welcome ${name}!`,
          subtitle: "Explore investment opportunities and market trends",
          cta: "Analyze Investments"
        };
      case "consultant":
        return {
          title: `Welcome ${name}!`,
          subtitle: "Access market insights and advisory data",
          cta: "View Market Analysis"
        };
      case "developer":
        return {
          title: `Welcome ${name}!`,
          subtitle: "Find development sites and market gaps",
          cta: "Explore Opportunities"
        };
      case "supplier":
        return {
          title: `Welcome ${name}!`,
          subtitle: "Connect with material and equipment opportunities",
          cta: "Find Supply Opportunities"
        };
      default:
        return {
          title: "Welcome to Sector Intelligence",
          subtitle: "Professional project discovery and market intelligence",
          cta: "Get Started"
        };
    }
  };

  const welcomeData = getRoleWelcomeMessage();

  const formatCurrency = (value: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      notation: 'compact',
      maximumFractionDigits: 1
    }).format(value);
  };

  return (
    <div className="bg-white">
      {/* Hero Section */}
      <div className="bg-gradient-to-br from-slate-50 via-blue-50 to-slate-100">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-20">
          <div className="text-center">
            <h1 className="text-4xl sm:text-5xl font-bold text-slate-900 mb-6">
              {welcomeData.title}
            </h1>
            <p className="text-xl text-slate-600 max-w-2xl mx-auto mb-8">
              {welcomeData.subtitle}
            </p>
            <Button 
              size="lg" 
              onClick={() => setLocation(getStartedPath())}
              className="px-8 py-3 text-lg"
            >
              {welcomeData.cta}
            </Button>
          </div>
        </div>
      </div>

      {/* Content Sections */}
      <div className="py-20" style={{ backgroundColor: '#0a1b3d' }}>
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          {/* Latest Projects */}
          <section className="mb-20">
            <div className="flex items-center justify-between mb-12">
              <div className="text-center flex-1">
                <div className="flex items-center justify-center mb-4">
                  <Newspaper className="w-8 h-8 text-teal-400 mr-3" />
                  <h2 className="text-3xl font-bold text-white">Latest Projects</h2>
                </div>
                <p className="text-lg text-gray-300 max-w-2xl mx-auto">
                  Discover the newest investment opportunities and development projects across the MENA region
                </p>
              </div>
              <Link href="/search">
                <Button variant="outline" className="ml-8">
                  View All Projects
                </Button>
              </Link>
            </div>

            <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
              {latestProjects.slice(0, 6).map((project) => (
                <div key={project.id} className="bg-white border border-gray-100 rounded-lg overflow-hidden hover:shadow-xl transition-shadow cursor-pointer">
                  <div className="p-6">
                    <div className="flex justify-between items-start mb-4">
                      <h3 
                        className="text-lg font-semibold text-gray-900 line-clamp-1 cursor-pointer hover:text-blue-600 transition-colors"
                        onClick={() => {
                          sessionStorage.setItem('previousPage', window.location.pathname);
                          setLocation(`/project/${project.id}`);
                        }}
                      >
                        {project.name}
                      </h3>
                      <span className="text-xs bg-gray-100 text-gray-600 px-2 py-1 rounded-full">
                        {project.status}
                      </span>
                    </div>
                    <div className="flex items-center text-sm text-gray-500 mb-4">
                      <MapPin size={14} className="mr-1" />
                      {project.location}
                    </div>
                    <p className="text-sm text-gray-600 mb-6 line-clamp-2">
                      {project.description}
                    </p>
                    <div className="grid grid-cols-2 gap-4 text-sm mb-4">
                      <div className="flex items-center">
                        <DollarSign size={14} className="mr-1 text-primary" />
                        <span className="font-medium text-gray-900">{formatCurrency(project.investment * 1000000)}</span>
                      </div>
                      <div className="flex items-center">
                        <Calendar size={14} className="mr-1 text-primary" />
                        <span className="text-gray-600">{project.completionDate}</span>
                      </div>
                    </div>
                    <span className="inline-block bg-primary/10 text-primary text-xs px-3 py-1 rounded-full">
                      {project.sector}
                    </span>
                  </div>
                </div>
              ))}
            </div>
          </section>

          {/* Trending Sectors */}
          <section className="mb-20 py-16 px-8 rounded-2xl" style={{ backgroundColor: '#00a7b2' }}>
            <div className="text-center mb-12">
              <div className="flex items-center justify-center mb-4">
                <TrendingUp className="w-8 h-8 text-white mr-3" />
                <h2 className="text-3xl font-bold text-white">Trending Sectors</h2>
              </div>
              <p className="text-lg text-gray-100 max-w-2xl mx-auto">
                Browse the most active investment sectors with growth opportunities
              </p>
            </div>

            <div className="grid md:grid-cols-2 lg:grid-cols-4 gap-8">
              {trendingSectors.slice(0, 4).map((sector, index) => (
                <div key={index} className="bg-white border border-gray-100 rounded-lg p-6 hover:shadow-lg transition-shadow cursor-pointer">
                  <div className="flex justify-between items-start mb-4">
                    <h3 className="font-semibold text-gray-900">{sector.name}</h3>
                    <span className="text-xs bg-primary/10 text-primary px-2 py-1 rounded-full">
                      {sector.growthRate}
                    </span>
                  </div>
                  <div className="space-y-3 text-sm text-gray-600">
                    <div className="flex justify-between">
                      <span>Projects:</span>
                      <span className="font-medium text-gray-900">{sector.projectCount}</span>
                    </div>
                    <div className="flex justify-between">
                      <span>Avg. Value:</span>
                      <span className="font-medium text-gray-900">{formatCurrency(sector.averageValue)}</span>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </section>

          {/* Featured Project Profiles */}
          <section className="mb-20">
            <div className="text-center mb-12">
              <div className="flex items-center justify-center mb-4">
                <Star className="w-8 h-8 text-yellow-400 mr-3" />
                <h2 className="text-3xl font-bold text-[#ffffff]">Featured Project Profiles</h2>
              </div>
              <p className="text-lg max-w-2xl mx-auto text-[#ffffff]">
                Explore detailed profiles of high-value investment opportunities
              </p>
            </div>

            <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
              {featuredProjects.map((project) => (
                <div key={project.id} className="bg-white border border-gray-100 rounded-lg overflow-hidden hover:shadow-xl transition-shadow">
                  <div className="p-6">
                    <div className="flex justify-between items-start mb-4">
                      <h3 
                        className="text-lg font-semibold text-gray-900 line-clamp-1 cursor-pointer hover:text-blue-600 transition-colors"
                        onClick={() => {
                          sessionStorage.setItem('previousPage', window.location.pathname);
                          setLocation(`/project/${project.id}`);
                        }}
                      >
                        {project.name}
                      </h3>
                      <span className="text-xs bg-gray-100 text-gray-600 px-2 py-1 rounded-full">
                        {project.status}
                      </span>
                    </div>
                    <div className="flex items-center text-sm text-gray-500 mb-4">
                      <MapPin size={14} className="mr-1" />
                      {project.location}
                    </div>
                    <p className="text-sm text-gray-600 mb-6 line-clamp-2">
                      {project.description}
                    </p>
                    <div className="grid grid-cols-2 gap-4 text-sm mb-4">
                      <div className="flex items-center">
                        <DollarSign size={14} className="mr-1 text-primary" />
                        <span className="font-medium text-gray-900">{formatCurrency(project.investment * 1000000)}</span>
                      </div>
                      <div className="flex items-center">
                        <Calendar size={14} className="mr-1 text-primary" />
                        <span className="text-gray-600">{project.completionDate}</span>
                      </div>
                    </div>
                    <span className="inline-block bg-primary/10 text-primary text-xs px-3 py-1 rounded-full">
                      {project.sector}
                    </span>
                  </div>
                </div>
              ))}
            </div>
          </section>
        </div>
      </div>
      {/* Call to Action */}
      <div className="bg-gray-50 py-20">
        <div className="max-w-4xl mx-auto text-center px-4 sm:px-6 lg:px-8">
          <h2 className="text-3xl font-bold text-gray-900 mb-4">
            Ready to Unlock Market Intelligence?
          </h2>
          <p className="text-lg text-gray-600 mb-8 max-w-2xl mx-auto">
            Join thousands of professionals accessing real-time market data and investment opportunities across the MENA region.
          </p>
          <Link href="/role-selection">
            <Button 
              size="lg" 
              className="bg-primary hover:bg-primary/90 text-white px-8 py-4 text-lg rounded-full"
            >
              Get Started Today
            </Button>
          </Link>
        </div>
      </div>
    </div>
  );
}

client/src/pages/investor-search.tsx.content:
import { useState, useEffect } from "react";
import { useQuery } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { Slider } from "@/components/ui/slider";
import { Checkbox } from "@/components/ui/checkbox";
import { Separator } from "@/components/ui/separator";
import { 
  Search, 
  Filter, 
  X, 
  Download, 
  Bookmark, 
  TrendingUp, 
  MapPin, 
  DollarSign, 
  Calendar,
  BarChart3,
  Building2,
  Zap,
  Factory,
  Fuel
} from "lucide-react";
import { ProjectCard } from "@/components/project-card";
import { Project, InvestorFilters } from "@shared/schema";
import { cn } from "@/lib/utils";
import { useToast } from "@/hooks/use-toast";

// Sector configuration with icons and sub-sectors
const SECTORS_CONFIG = {
  "Real Estate": {
    icon: Building2,
    color: "#0a1b3d",
    subSectors: ["Residential", "Commercial", "Hospitality", "Retail", "Mixed-use"]
  },
  "Infrastructure": {
    icon: BarChart3,
    color: "#00a7b2",
    subSectors: ["Roads", "Bridges", "Airports", "Ports", "Utilities Infrastructure"]
  },
  "Energy": {
    icon: Zap,
    color: "#f59e0b",
    subSectors: ["Renewable Energy (Solar, Wind)", "Power Plants", "Energy Storage"]
  },
  "Industry": {
    icon: Factory,
    color: "#10b981",
    subSectors: ["Industrial Parks", "Manufacturing Zones", "Logistics Hubs"]
  },
  "Oil & Gas": {
    icon: Fuel,
    color: "#ef4444",
    subSectors: ["Refineries", "Petrochemical Plants", "Distribution Facilities"]
  }
};

const COUNTRIES = ["Saudi Arabia", "United Arab Emirates", "Qatar", "Kuwait", "Bahrain", "Oman"];
const STATUSES = ["Planning", "Under Construction", "Nearing Completion", "Completed / Operational"];
const RISK_LEVELS = ["Low", "Medium", "High"];

export default function InvestorSearch() {
  const { toast } = useToast();
  const [filters, setFilters] = useState<InvestorFilters>({
    country: "Saudi Arabia"
  });
  const [savedSearches, setSavedSearches] = useState<string[]>([]);
  const [comparisonProjects, setComparisonProjects] = useState<Project[]>([]);

  const { data: projects = [], isLoading } = useQuery<Project[]>({
    queryKey: ["/api/projects", filters],
    enabled: true,
  });

  const { data: cities = [] } = useQuery<string[]>({
    queryKey: ["/api/cities", filters.country],
    enabled: !!filters.country,
  });

  const { data: districts = [] } = useQuery<string[]>({
    queryKey: ["/api/districts", filters.country, filters.city],
    enabled: !!filters.country && !!filters.city,
  });

  const handleFilterChange = (key: keyof InvestorFilters, value: any) => {
    const newFilters = { ...filters, [key]: value };

    // Reset dependent filters
    if (key === "country") {
      newFilters.city = undefined;
      newFilters.district = undefined;
    } else if (key === "city") {
      newFilters.district = undefined;
    } else if (key === "sector") {
      newFilters.subSector = undefined;
    }

    setFilters(newFilters);
  };

  const removeFilter = (key: keyof InvestorFilters) => {
    const newFilters = { ...filters };
    delete newFilters[key];
    setFilters(newFilters);
  };

  const clearAllFilters = () => {
    setFilters({ country: "Saudi Arabia" });
  };

  const saveCurrentSearch = () => {
    const searchName = `Search ${new Date().toLocaleDateString()}`;
    setSavedSearches(prev => [...prev, searchName]);
    toast({
      title: "Search Saved",
      description: "Your investment search criteria has been saved.",
    });
  };

  const exportResults = () => {
    toast({
      title: "Export Started",
      description: "Your investment analysis report is being prepared.",
    });
  };

  const getActiveFilterCount = () => {
    return Object.values(filters).filter(value => value !== undefined && value !== "").length;
  };

  const selectedSectorConfig = filters.sector ? SECTORS_CONFIG[filters.sector as keyof typeof SECTORS_CONFIG] : null;

  if (isLoading) {
    return (
      <div className="min-h-screen bg-slate-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-primary mx-auto"></div>
          <p className="mt-4 text-slate-600">Loading investment opportunities...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-slate-50">
      {/* Header */}
      <div className="bg-white border-b border-slate-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="py-6">
            <div className="flex items-center justify-between">
              <div>
                <h1 className="text-3xl font-bold text-slate-900">Investor Search Module</h1>
                <p className="text-slate-600 mt-1">
                  Discover and analyze investment opportunities across MENA region sectors
                </p>
              </div>
              <div className="flex items-center space-x-3">
                <Button variant="outline" onClick={saveCurrentSearch}>
                  <Bookmark className="mr-2" size={16} />
                  Save Search
                </Button>
                <Button onClick={exportResults}>
                  <Download className="mr-2" size={16} />
                  Export Analysis
                </Button>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="grid lg:grid-cols-4 gap-8">

          {/* Advanced Filters Sidebar */}
          <div className="lg:col-span-1">
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center justify-between">
                  <span className="flex items-center">
                    <Filter className="mr-2" size={18} />
                    Investment Filters
                  </span>
                  {getActiveFilterCount() > 1 && (
                    <Badge variant="secondary">{getActiveFilterCount() - 1}</Badge>
                  )}
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-6">

                {/* Location Filters */}
                <div>
                  <Label className="text-sm font-medium text-slate-700">Location</Label>
                  <div className="space-y-3 mt-2">
                    <div>
                      <Label htmlFor="country" className="text-xs text-slate-500">Country</Label>
                      <Select 
                        value={filters.country || ""} 
                        onValueChange={(value) => handleFilterChange("country", value)}
                      >
                        <SelectTrigger>
                          <SelectValue placeholder="Select country" />
                        </SelectTrigger>
                        <SelectContent>
                          {COUNTRIES.map(country => (
                            <SelectItem key={country} value={country}>{country}</SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                    </div>

                    {filters.country && (
                      <div>
                        <Label htmlFor="city" className="text-xs text-slate-500">City</Label>
                        <Select 
                          value={filters.city || ""} 
                          onValueChange={(value) => handleFilterChange("city", value)}
                        >
                          <SelectTrigger>
                            <SelectValue placeholder="Select city" />
                          </SelectTrigger>
                          <SelectContent>
                            <SelectItem value="">All Cities</SelectItem>
                            {cities.map(city => (
                              <SelectItem key={city} value={city}>{city}</SelectItem>
                            ))}
                          </SelectContent>
                        </Select>
                      </div>
                    )}

                    {filters.city && (
                      <div>
                        <Label htmlFor="district" className="text-xs text-slate-500">District (Optional)</Label>
                        <Select 
                          value={filters.district || ""} 
                          onValueChange={(value) => handleFilterChange("district", value)}
                        >
                          <SelectTrigger>
                            <SelectValue placeholder="Select district" />
                          </SelectTrigger>
                          <SelectContent>
                            <SelectItem value="">All Districts</SelectItem>
                            {districts.map(district => (
                              <SelectItem key={district} value={district}>{district}</SelectItem>
                            ))}
                          </SelectContent>
                        </Select>
                      </div>
                    )}
                  </div>
                </div>

                <Separator />

                {/* Sector Selection */}
                <div>
                  <Label className="text-sm font-medium text-slate-700">Sector</Label>
                  <div className="space-y-3 mt-2">
                    <Select 
                      value={filters.sector || ""} 
                      onValueChange={(value) => handleFilterChange("sector", value)}
                    >
                      <SelectTrigger>
                        <SelectValue placeholder="Select main sector" />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="">All Sectors</SelectItem>
                        {Object.entries(SECTORS_CONFIG).map(([sector, config]) => {
                          const Icon = config.icon;
                          return (
                            <SelectItem key={sector} value={sector}>
                              <div className="flex items-center">
                                <Icon size={16} className="mr-2" style={{ color: config.color }} />
                                {sector}
                              </div>
                            </SelectItem>
                          );
                        })}
                      </SelectContent>
                    </Select>

                    {selectedSectorConfig && (
                      <div>
                        <Label htmlFor="subSector" className="text-xs text-slate-500">Sub-Sector</Label>
                        <Select 
                          value={filters.subSector || ""} 
                          onValueChange={(value) => handleFilterChange("subSector", value)}
                        >
                          <SelectTrigger>
                            <SelectValue placeholder="Select sub-sector" />
                          </SelectTrigger>
                          <SelectContent>
                            <SelectItem value="">All Sub-Sectors</SelectItem>
                            {selectedSectorConfig.subSectors.map(subSector => (
                              <SelectItem key={subSector} value={subSector}>{subSector}</SelectItem>
                            ))}
                          </SelectContent>
                        </Select>
                      </div>
                    )}
                  </div>
                </div>

                <Separator />

                {/* Project Status */}
                <div>
                  <Label className="text-sm font-medium text-slate-700">Project Status</Label>
                  <div className="space-y-2 mt-2">
                    {STATUSES.map(status => (
                      <div key={status} className="flex items-center space-x-2">
                        <Checkbox
                          id={`status-${status}`}
                          checked={filters.status === status}
                          onCheckedChange={(checked) => 
                            handleFilterChange("status", checked ? status : undefined)
                          }
                        />
                        <label 
                          htmlFor={`status-${status}`}
                          className="text-sm text-slate-600 cursor-pointer"
                        >
                          {status}
                        </label>
                      </div>
                    ))}
                  </div>
                </div>

                <Separator />

                {/* Investment Range */}
                <div>
                  <Label className="text-sm font-medium text-slate-700">Investment Range (USD Millions)</Label>
                  <div className="space-y-3 mt-2">
                    <div className="grid grid-cols-2 gap-2">
                      <div>
                        <Label htmlFor="minInvestment" className="text-xs text-slate-500">Min</Label>
                        <Input
                          id="minInvestment"
                          type="number"
                          placeholder="0"
                          value={filters.minInvestment || ""}
                          onChange={(e) => handleFilterChange("minInvestment", Number(e.target.value) || undefined)}
                        />
                      </div>
                      <div>
                        <Label htmlFor="maxInvestment" className="text-xs text-slate-500">Max</Label>
                        <Input
                          id="maxInvestment"
                          type="number"
                          placeholder="1000"
                          value={filters.maxInvestment || ""}
                          onChange={(e) => handleFilterChange("maxInvestment", Number(e.target.value) || undefined)}
                        />
                      </div>
                    </div>
                  </div>
                </div>

                <Separator />

                {/* ROI Range */}
                <div>
                  <Label className="text-sm font-medium text-slate-700">Expected ROI (%)</Label>
                  <div className="space-y-3 mt-2">
                    <div className="grid grid-cols-2 gap-2">
                      <div>
                        <Label htmlFor="minRoi" className="text-xs text-slate-500">Min</Label>
                        <Input
                          id="minRoi"
                          type="number"
                          placeholder="0"
                          value={filters.minRoi || ""}
                          onChange={(e) => handleFilterChange("minRoi", Number(e.target.value) || undefined)}
                        />
                      </div>
                      <div>
                        <Label htmlFor="maxRoi" className="text-xs text-slate-500">Max</Label>
                        <Input
                          id="maxRoi"
                          type="number"
                          placeholder="50"
                          value={filters.maxRoi || ""}
                          onChange={(e) => handleFilterChange("maxRoi", Number(e.target.value) || undefined)}
                        />
                      </div>
                    </div>
                  </div>
                </div>

                <Separator />

                {/* Risk Level */}
                <div>
                  <Label className="text-sm font-medium text-slate-700">Risk Assessment</Label>
                  <div className="space-y-2 mt-2">
                    {RISK_LEVELS.map(level => (
                      <div key={level} className="flex items-center space-x-2">
                        <Checkbox
                          id={`risk-${level}`}
                          checked={filters.riskLevel === level}
                          onCheckedChange={(checked) => 
                            handleFilterChange("riskLevel", checked ? level : undefined)
                          }
                        />
                        <label 
                          htmlFor={`risk-${level}`}
                          className="text-sm text-slate-600 cursor-pointer"
                        >
                          {level} Risk
                        </label>
                      </div>
                    ))}
                  </div>
                </div>

                {/* Clear Filters */}
                {getActiveFilterCount() > 1 && (
                  <Button 
                    variant="outline" 
                    onClick={clearAllFilters}
                    className="w-full"
                  >
                    <X className="mr-2" size={16} />
                    Clear All Filters
                  </Button>
                )}
              </CardContent>
            </Card>
          </div>

          {/* Results Section */}
          <div className="lg:col-span-3">
            <div className="space-y-6">

              {/* Results Summary */}
              <Card>
                <CardContent className="p-6">
                  <div className="flex items-center justify-between">
                    <div>
                      <h2 className="text-xl font-semibold text-slate-900">Investment Opportunities</h2>
                      <p className="text-slate-600">
                        Found <span className="font-medium">{projects.length} projects</span> matching your investment criteria
                      </p>
                    </div>
                    <div className="flex items-center space-x-2">
                      <TrendingUp className="text-green-500" size={20} />
                      <span className="text-sm text-slate-600">Market confidence: High</span>
                    </div>
                  </div>
                </CardContent>
              </Card>

              {/* Active Filters Display */}
              {getActiveFilterCount() > 1 && (
                <Card>
                  <CardContent className="p-4">
                    <div className="flex items-center justify-between">
                      <span className="text-sm font-medium text-slate-700">Active Filters:</span>
                      <Button 
                        variant="ghost" 
                        size="sm" 
                        onClick={clearAllFilters}
                        className="text-slate-500 hover:text-slate-700"
                      >
                        Clear All
                      </Button>
                    </div>
                    <div className="flex flex-wrap gap-2 mt-2">
                      {Object.entries(filters).map(([key, value]) => {
                        if (!value || key === "country") return null;
                        return (
                          <Badge key={key} variant="secondary" className="flex items-center">
                            <span className="text-xs">{String(value)}</span>
                            <Button
                              variant="ghost"
                              size="sm"
                              onClick={() => removeFilter(key as keyof InvestorFilters)}
                              className="ml-1 h-auto p-0 text-slate-500 hover:text-slate-700"
                            >
                              <X size={12} />
                            </Button>
                          </Badge>
                        );
                      })}
                    </div>
                  </CardContent>
                </Card>
              )}

              {/* Project Results */}
              <div className="grid md:grid-cols-2 gap-6">
                {projects.map((project) => (
                  <ProjectCard
                    key={project.id}
                    project={project}
                    onToggleFavorite={() => {}}
                    onViewDetails={() => {}}
                    isFavorite={false}
                  />
                ))}
              </div>

              {projects.length === 0 && (
                <Card className="text-center py-12">
                  <CardContent>
                    <Search className="mx-auto h-12 w-12 text-slate-400 mb-4" />
                    <h3 className="text-lg font-medium text-slate-900 mb-2">No Investment Opportunities Found</h3>
                    <p className="text-slate-600 mb-4">
                      Try adjusting your search criteria or expanding your location filters.
                    </p>
                    <Button onClick={clearAllFilters}>
                      Clear Filters & Browse All
                    </Button>
                  </CardContent>
                </Card>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

