    title: "Supplier",
    description: "Materials & equipment for projects",
    icon: Truck,
    features: ["Supply Opportunities", "Material Demand", "Project Schedules"],
    color: "supplier",
  },
  {
    id: "others" as UserRole,
    title: "Others",
    description: "General project discovery & insights",
    icon: CircleUser,
    features: ["Project Discovery", "Market Overview", "Industry Trends"],
    color: "others",
  },
];

export default function RoleSelection() {
  const [selectedRole, setSelectedRole] = useState<UserRole | null>(null);
  const [, setLocation] = useLocation();

  const handleContinue = () => {
    if (selectedRole) {
      // Clear any existing role data first
      localStorage.removeItem("selectedRole");
      localStorage.removeItem("userName");

      // Store new role in localStorage for persistence
      localStorage.setItem("selectedRole", selectedRole);

      console.log("Role selection - storing role:", selectedRole);

      // Trigger custom event to notify other components of role change
      window.dispatchEvent(new CustomEvent("roleChanged"));

      // Route to homepage after role selection
      setLocation("/");
    }
  };

  return (
    <div className="bg-gradient-to-br from-slate-50 via-blue-50 to-slate-100">
      <div className="pt-16 pb-12">
        <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
          <div className="mb-8">
            <h1 className="text-4xl sm:text-5xl font-bold text-slate-900 mb-4">
              Sector Intelligence
            </h1>
            <p className="text-xl text-slate-600 max-w-2xl mx-auto">
              Professional project discovery and market intelligence platform for real estate and development professionals
            </p>
          </div>

          <div className="flex flex-wrap justify-center gap-6 mt-12">
            {roles.map((role) => {
              const Icon = role.icon;
              const isSelected = selectedRole === role.id;

              return (
                <Card
                  key={role.id}
                  className={cn(
                    "role-card border-2 border-transparent hover:shadow-xl transition-all duration-300 cursor-pointer w-64 h-48",
                    isSelected && "border-primary bg-blue-50 selected"
                  )}
                  onClick={() => setSelectedRole(role.id)}
                >
                  <CardContent className="p-8 text-center h-full flex flex-col justify-center">
                    <div 
                      className="w-16 h-16 rounded-full flex items-center justify-center mx-auto mb-4"
                      style={{ backgroundColor: `${roleColors[role.id]}10` }}
                    >
                      <Icon 
                        size={24} 
                        style={{ color: roleColors[role.id] }}
                      />
                    </div>
                    <h3 className="text-xl font-semibold text-slate-900 mb-6">{role.title}</h3>
                    <div className="flex items-center justify-center">
                      <p className="text-slate-700 text-sm pl-[5px] pr-[5px]">{role.description}</p>
                    </div>
                  </CardContent>
                </Card>
              );
            })}
          </div>

          <div className="mt-12">
            <Button
              size="lg"
              onClick={handleContinue}
              disabled={!selectedRole}
              className={cn(
                "px-8 py-3 text-lg",
                !selectedRole && "opacity-50 cursor-not-allowed"
              )}
            >
              <ArrowRight className="mr-2" size={20} />
              {selectedRole ? "Continue to Search" : "Select Your Role to Continue"}
            </Button>
          </div>
        </div>
      </div>
    </div>
  );
}

